// import sampleData from "./data/covids.json";
import './index.css';
import sampleData from "./data/data.json";
import fullData from "./data/full.json";
import janData from "./data/jan.json";
import febData from "./data/feb.json";
import React, { useState, useEffect, useRef} from "react";
import "antd/dist/antd.css";
import { StaticMap } from "react-map-gl";
import DeckGL from "@deck.gl/react";
import { AmbientLight, PointLight, LightingEffect } from "@deck.gl/core";
import { HexagonLayer } from "@deck.gl/aggregation-layers";
import { Typography } from "antd";
import { Statistic } from "antd";
import { CSSTransition } from 'react-transition-group'

const { Title, Text } = Typography;


// Set your mapbox access token here
const mapboxApiAccessToken =
  "pk.eyJ1IjoidmVyYTFrIiwiYSI6ImNrZjQ3aWJoczA4ZGQydm43cXFjcW5peTkifQ.sYJ99dX7B8QyPgV_-TszTA"; // public token URL

const ambientLight = new AmbientLight({
  color: [255, 255, 255],
  intensity: 1.0
});

const pointLight1 = new PointLight({
  color: [255, 255, 255],
  intensity: 0.8,
  position: [-0.144528, 49.739968, 80000]
});

const pointLight2 = new PointLight({
  color: [255, 255, 255],
  intensity: 0.8,
  position: [-3.807751, 54.104682, 8000]
});   

const lightingEffect = new LightingEffect({ambientLight, pointLight1, pointLight2});

const material = {
  ambient: 0.64,
  diffuse: 0.6,
  shininess: 32,
  specularColor: [51, 51, 51]
};

const INITIAL_VIEW_STATE = {
  longitude: -122,
  latitude: 38,
  zoom: 6,
  minZoom: 2,
  maxZoom: 15,
  pitch: 40.5,
  bearing: -27
};

export const colorRange = [
  [1, 152, 189],
  [73, 227, 206],
  [216, 254, 181],
  [254, 237, 177],
  [254, 173, 84],
  [209, 55, 78]
];
/**

  [${randomData} * x / 255, 120, 255]
 */
export const heatmapColorRange = [
  [255, 255, 178],
  [254, 217, 118],
  [254, 178, 76],
  [253, 141, 60],
  [240, 59, 32],
  [189, 0, 38],
];


function getTooltip({ object }) {
  if (!object) {
    return null;
  }
  console.log(object);
  const lat = object.position[1];
  const lng = object.position[0];
  const airQ = object.colorValue;
  const infectionCount = object.elevationValue;

  return `\
    Latitude: ${Number.isFinite(lat) ? lat.toFixed(6) : ""}
    Longitude: ${Number.isFinite(lng) ? lng.toFixed(6) : ""}
    Air Quality: ${airQ} 
    Number of Infections: ${infectionCount} `;
}

function getInfectionsCount(data) {
  var count = 0;
  for (var i = 0; i < data.length; i++) {

    count += data[i].infections;
  }
  return count;
}

export default function App({
  intensity = 1,
  threshold = 0.1,
  radiusPixels = 30,
  mapStyle = "mapbox://styles/mapbox/dark-v9",
  coverage = 1,
}) {
  // USESTATES FOR DATA AND VISUALIZATION
  let data = useRef(sampleData);
  const [airPollutant, setAirPollutant] = useState("AQI");
  const [month, setMonth] = useState("ALL");
  function handleData(e) {
    if (e.key === "ALL") {
      data = fullData;
    } else if (e.key === "jan") {
      data = janData;
    } else if (e.key === "feb") {
      data = febData;
    }
    console.log(e.key);
  }
// NAVBAR FUNCTIONS

  function Navbar(props) {
    return (
      <nav className="navbar">
        <ul className="navbar-nav">{props.children}</ul>
      </nav>
    );
  }

  function NavItem(props) {
    const [open, setOpen] = useState(false);

    return (
      <li className="nav-item">
        <a href="#" className="icon-button" onClick={() => setOpen(!open)}>
          {props.icon}
        </a>
        {open && props.children}
      </li>
    );
  }

  function DropdownMenu() {
    const [activeMenu, setActiveMenu] = useState('main');
    const [menuHeight, setMenuHeight] = useState(null);
    const dropdownRef = useRef(null);

    useEffect(() => {
      setMenuHeight(dropdownRef.current?.firstChild.offsetHeight)
    }, [])

    function calcHeight(el) {
      const height = el.offsetHeight;
      setMenuHeight(height);
    }

    function DropdownItem(props) {
      return (
        <a href="#" className="menu-item" onClick={() => props.goToMenu && setActiveMenu(props.goToMenu)}>
          <span className="icon-button">{props.leftIcon}</span>
          {props.children}
          <span className="icon-right">{props.rightIcon}</span>
        </a>
      );
    }
    return (
      <div className="dropdown" style={{ height: menuHeight }} ref={dropdownRef}>

        <CSSTransition
          in={activeMenu === 'main'}
          timeout={500}
          classNames="menu-primary"
          unmountOnExit
          onEnter={calcHeight}>
          <div className="menu">
            <DropdownItem
              leftIcon="⚙"
              goToMenu="time">
              Change Time Range
            </DropdownItem>
            <DropdownItem
              leftIcon="☁"
              goToMenu="airPollutant">
              Change Air Pollutant
            </DropdownItem>

          </div>
        </CSSTransition>

        <CSSTransition
          in={activeMenu === 'time'}
          timeout={500}
          classNames="menu-secondary"
          unmountOnExit
          onEnter={calcHeight}>
          <div className="menu">
            <DropdownItem goToMenu="main" leftIcon="◀️">
              Go Back
            </DropdownItem>
            <a onClick={handleData}>
            <DropdownItem leftIcon="" key="ALL" >All</DropdownItem>
            <DropdownItem leftIcon="" key="jan" >Jan</DropdownItem>
            <DropdownItem leftIcon="" key="feb" >Feb</DropdownItem>
            <DropdownItem leftIcon="">March</DropdownItem>
            <DropdownItem leftIcon="">April</DropdownItem>
            <DropdownItem leftIcon="">May</DropdownItem>
            <DropdownItem leftIcon="">June</DropdownItem>
